mapper.select:


     and gt.data_name = #{dataName}
     and lower(gt.table_name) like concat(concat('%', #{tableName}), '%')
     and lower(gt.table_comment) like concat(concat('%', #{tableComment}), '%')
    and gt.create_time between #{params.beginTime} and #{params.endTime}

order by gt.create_time desc

select.if:
select table_name, table_comment, create_time, update_time from information_schema.tables
where table_name NOT LIKE 'pj_%' and table_schema = (select database())
and table_name = #{tableName}
