mapper.select:


     and sm.menu_name like concat(concat('%', #{menuName}), '%')
     and sm.component_name like concat(concat('%', #{componentName}), '%')
     and sm.visible = #{visible}
     and sm.status = #{status}

order by sm.parent_id, sm.order_num

select distinct sm.menu_id, sm.parent_id, sm.menu_name, sm.path, sm.component, sm.query_param, sm.visible, sm.status, sm.perms, sm.is_frame, sm.is_cache, sm.menu_type, sm.icon, sm.order_num, sm.create_time from sys_menu sm left join sys_role_menu rm on sm.menu_id = rm.menu_id left join sys_user_role sur on rm.role_id = sur.role_id
and sur.user_id = #{userId} and sm.menu_name like concat(concat('%', #{menuName}), '%') and sm.component_name like concat(concat('%', #{componentName}), '%') and sm.visible = #{visible} and sm.status = #{status}
order by sm.parent_id, sm.order_num

select m.menu_id from sys_menu m left join sys_role_menu rm on m.menu_id = rm.menu_id where rm.role_id = #{roleId}
and m.menu_id not in (select m.parent_id from sys_menu m inner join sys_role_menu rm on m.menu_id = rm.menu_id and rm.role_id = #{roleId})
order by m.parent_id, m.order_num
