mapper.select:
select u.user_id, u.dept_id, u.nick_name, u.user_name, u.email, u.avatar, u.phonenumber, u.sex,
u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark, d.dept_name, d.leader from
sys_user u
left join sys_dept d on u.dept_id = d.dept_id

    and u.del_flag = '0'
     and u.user_id = #{userId}

        and u.dept_id in

            #{item}


     and u.user_name like concat('%', #{userName}, '%')
     and u.phonenumber like concat('%', #{phonenumber}, '%')
     and u.`status` = #{status}
    and u.create_time between #{params.beginTime} and #{params.endTime}

order by u.create_time desc

select distinct u.user_id, u.dept_id, u.user_name, u.nick_name, u.email, u.phonenumber, u.status, u.create_time from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role sur on u.user_id = sur.user_id left join sys_role r on r.role_id = sur.role_id
and u.del_flag = '0' and r.role_id = #{roleId} and u.user_name like concat('%', #{userName}, '%') and u.phonenumber like concat('%', #{phonenumber}, '%') and u.`status` = #{status}
order by u.create_time desc
